mapper.select:
select ML_SQ, ML_GRP_SQ, ML_STT_SQ, EMP_SQ, ML_SND_ADD, ML_RCV_ADD, ML_SEND_DATE, ML_TTL, ML_CTT, ML_FL_NM, ML_FL_RT
from ML 
where ML_SQ = #{ml_sq}

select ML_SQ, ML_GRP_SQ, ML_STT_SQ, EMP_SQ, ML_SND_ADD, ML_RCV_ADD, ML_SEND_DATE, ML_TTL, ML_CTT, ML_FL_NM, ML_FL_RT
from ML 
where ML_SQ = #{ml_sq}

select ML_GRP_SQ, ML_GRP_NM 
from ML_GRP
where ML_GRP_SQ = #{ml_grp_sq}

mapper.delete:
delete from ML 
where ML_GRP_SQ = #{ml_grp_sq}||#{emp_sq} 
and ML_SQ = #{ml_sq}
