mapper.update:
UPDATE SCD
SET scd_div_sq = #{scd_div_sq}, scd_nm = #{scd_nm}, scd_pst = #{scd_pst}, scd_sday = #{scd_sday}, scd_eday = #{scd_eday}, scd_dct = #{scd_dct}
WHERE scd_sq = #{scd_sq}

mapper.select:
SELECT COUNT(*)
FROM SCD
WHERE SCD_SDAY like #{month}||'/'||'%'||'/'||#{year}
AND EMP_SQ = #{emp_sq}
